// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

interface ITokenManager {
    function addAcceptedToken(address merchant, address token) external;
    function removeAcceptedToken(address merchant, address token) external;
    function isTokenAccepted(address merchant, address token) external view returns (bool);
    function viewAcceptedTokens(address merchant) external view returns (address[] memory);
}

contracts/MerchantManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "./interfaces/IMerchantManager.sol";

contract MerchantManager is IMerchantManager {
    address public owner;

    struct Store {
        uint256 transactionCountToday;
        uint256 transactionVolumeToday;
        uint256 totalTransactionCount;
        uint256 totalTransactionVolume;
        uint256 creationDate;
        uint256 lastTransactionDate;
    }

    struct Merchant {
        bool isRegistered;
        address walletAddress;
        bool isPremium;
        uint256 registrationDate;
        address[] acceptedTokens; 
        uint256 storeCount;
        mapping(uint256 => Store) stores; // Stores mapping
    }

    mapping(address => Merchant) public merchants;
    address[] public merchantAddresses;

    constructor() {
        owner = msg.sender;
    }

    event MerchantRegistered(address indexed merchant, bool isPremium);

  function registerMerchant(bool isPremium, address[] calldata acceptedTokens) external override {
        require(!merchants[msg.sender].isRegistered, "Merchant already registered.");

        Merchant storage merchant = merchants[msg.sender];
        merchant.isRegistered = true;
        merchant.walletAddress = msg.sender;
        merchant.isPremium = isPremium;
        merchant.registrationDate = block.timestamp;
        merchant.storeCount = 0;
        delete merchant.acceptedTokens;
        for (uint i = 0; i < acceptedTokens.length; i++) {
            merchant.acceptedTokens.push(acceptedTokens[i]);
        }

        merchantAddresses.push(msg.sender);
        emit MerchantRegistered(msg.sender, isPremium);
    }
    // function isMerchantRegistered(address walletAddress) external view returns (bool) {
    //     return merchants[walletAddress].isRegistered;
    // }
    function isMerchantRegistered(address merchant) public view returns (bool) {
    return merchants[merchant].isRegistered;
}

    function getMerchantInfo(address walletAddress) external view returns (bool isRegistered, bool isPremium, uint256 registrationDate, uint256 storeCount, address[] memory acceptedTokens) {
        Merchant storage merchant = merchants[walletAddress];
        return (merchant.isRegistered, merchant.isPremium, merchant.registrationDate, merchant.storeCount, merchant.acceptedTokens);
    }

    function getMerchantCount() external view returns (uint256) {
        return merchantAddresses.length;
    }

    function updateAcceptedTokens(address walletAddress, address[] calldata newTokens) external {
        require(msg.sender == walletAddress, "Unauthorized: can only update your own tokens");
        require(merchants[walletAddress].isRegistered, "Merchant not registered.");
        merchants[walletAddress].acceptedTokens = newTokens;
    }
}

contracts/StoreManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "./MerchantManager.sol";

contract StoreManager is MerchantManager {
    event StoreAdded(address indexed merchant, uint256 storeId);

    // Allows any registered merchant to add a store. Non-premium merchants can only have one store.
    function addStore() public {
        require(merchants[msg.sender].isRegistered, "StoreManager: Merchant not registered");
        if (!merchants[msg.sender].isPremium) {
            require(merchants[msg.sender].storeCount == 0, "StoreManager: Non-premium merchants can only have one store");
        }

        uint256 storeId = merchants[msg.sender].storeCount;
        Store storage store = merchants[msg.sender].stores[storeId];
        store.transactionCountToday = 0;
        store.transactionVolumeToday = 0;
        store.totalTransactionCount = 0;
        store.totalTransactionVolume = 0;
        store.creationDate = block.timestamp;
        store.lastTransactionDate = 0;

        merchants[msg.sender].storeCount++;
        emit StoreAdded(msg.sender, storeId);
    }

    // Function to retrieve information about a store
   function getStoreInfo(address walletAddress, uint256 storeId) public view returns (uint256, uint256, uint256, uint256, uint256, uint256) {
    Store storage store = merchants[walletAddress].stores[storeId];
    return (
        store.transactionCountToday,
        store.transactionVolumeToday,
        store.totalTransactionCount,
        store.totalTransactionVolume,
        store.creationDate,
        store.lastTransactionDate
    );
}

}

contracts/TokenManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "./MerchantManager.sol";
import "./interfaces/ITokenManager.sol";

contract TokenManager is MerchantManager, ITokenManager {
    mapping(address => bool) public supportedTokens; // Global list of supported tokens
    mapping(address => address[]) public merchantAcceptedTokens; // Tokens accepted by each merchant

    event TokenSupported(address token);
    event TokenUnsupported(address token);
    event TokenAdded(address indexed merchant, address token);
    event TokenRemoved(address indexed merchant, address token);

    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can perform this action");
        _;
    }

    function supportNewToken(address token) public onlyOwner {
        require(!supportedTokens[token], "Token already supported");
        supportedTokens[token] = true;
        emit TokenSupported(token);
    }

    function stopSupportingToken(address token) public onlyOwner {
        require(supportedTokens[token], "Token not supported");
        supportedTokens[token] = false;
        emit TokenUnsupported(token);
    }

    function addAcceptedToken(address merchant, address token) public {
        require(isMerchantRegistered(merchant), "Merchant not registered.");
        require(msg.sender == merchant || msg.sender == owner, "Unauthorized");
        require(supportedTokens[token], "Token not supported by the platform");
        require(!isTokenAccepted(merchant, token), "Token already accepted.");

        merchantAcceptedTokens[merchant].push(token);
        emit TokenAdded(merchant, token);
    }

    function removeAcceptedToken(address merchant, address token) public {
        require(isMerchantRegistered(merchant), "Merchant not registered.");
        require(msg.sender == merchant || msg.sender == owner, "Unauthorized");
        require(isTokenAccepted(merchant, token), "Token not accepted.");

        address[] storage tokens = merchantAcceptedTokens[merchant];
        for (uint i = 0; i < tokens.length; i++) {
            if (tokens[i] == token) {
                tokens[i] = tokens[tokens.length - 1];
                tokens.pop();
                emit TokenRemoved(merchant, token);
                break;
            }
        }
    }

    function isTokenAccepted(address merchant, address token) public view returns (bool) {
        address[] memory tokens = merchantAcceptedTokens[merchant];
        for (uint i = 0; i < tokens.length; i++) {
            if (tokens[i] == token) {
                return true;
            }
        }
        return false;
    }

    function viewAcceptedTokens(address merchant) public view returns (address[] memory) {
        require(isMerchantRegistered(merchant), "Merchant not registered.");
        return merchantAcceptedTokens[merchant];
    }
}

